SELECT DISTINCT hp.party_id, hp.party_number , hp.party_name           AS NOMBRE, hp.creation_date        AS fecha_creacion, DECODE(hp.jgzz_fiscal_code, NULL, hp.ATTRIBUTE1, hp.jgzz_fiscal_code)    AS NIT , hp.address1             AS DIRECCION , hp.city                 AS CIUDAD , hp.state                AS DEPARTAMENTO, hp.url                  AS URL , hp.email_address        AS CORREO , hp.PRIMARY_PHONE_NUMBER AS TELEFONO, hp.attribute17          AS LONGITUD, dsv.DIAS_SIN_VISITA     AS diasSinVisita FROM ar.hz_parties hp, hz_code_assignments cod, IMCM_DIAS_SIN_VISITA dsv, FND_USER WHERE party_type         = 'ORGANIZATION' AND dsv.PARTY_ID      = hp.PARTY_ID AND dsv.USER_ID    = FND_USER.USER_ID AND cod.owner_table_name = 'HZ_PARTIES' AND cod.class_category   = 'Estado del Cliente' AND hp.ATTRIBUTE16 LIKE '%C%' AND (cod.CLASS_CODE LIKE 'Activo' OR cod.CLASS_CODE LIKE 'Prospecto') AND cod.owner_table_id = hp.party_id AND hp.attribute17    IS NOT NULL AND FND_USER.USER_NAME LIKE #v_asesor