SELECT p.JGZZ_FISCAL_CODE ,n.CREATION_DATE ,ntl.NOTES ,ntl.NOTES_DETAIL, rs.SOURCE_NAME FROM JTF_NOTES_B n, JTF_NOTES_TL ntl, HZ_PARTIES p, JTF_RS_RESOURCE_EXTNS rs WHERE ntl.JTF_NOTE_ID = n.JTF_NOTE_ID AND n.SOURCE_OBJECT_ID =  p.PARTY_ID AND n.CREATED_BY = rs.USER_ID AND p.JGZZ_FISCAL_CODE like #v_nit AND rs.USER_NAME like #v_asesor ORDER BY n.CREATION_DATE desc