SELECT DISTINCT org.INVENTORY_ITEM_ID, org.DESCRIPTION, UPPER( (SELECT DISTINCT vv.DESCRIPTION FROM fnd_flex_value_sets vs,   fnd_flex_values_vl vv WHERE vs.flex_value_set_name= 'IMO_MARCA_PROV' AND vs.flex_value_set_id    = vv.flex_value_set_id AND vv.flex_value           = ORG.segment4 ))           AS Marca, org.SEGMENT6 AS modelo, nvl(li.lista, 0) as precio_lista FROM MTL_SYSTEM_ITEMS_B org INNER JOIN AS_LEAD_LINES_ALL ON (org.INVENTORY_ITEM_ID       = AS_LEAD_LINES_ALL.INVENTORY_ITEM_ID) INNER JOIN  (SELECT Qlh.CURRENCY_CODE,     (Qll.Operand) Lista,  qll.Product_Attr_Value inventory_item_id, qll.product_uom_code FROM Qp_List_Lines_v Qll,   Qp_List_Headers_All Qlh WHERE Qlh.LIST_HEADER_ID = qll.list_header_id AND Qlh.LIST_HEADER_ID   = 4240963 AND TRUNC(SYSDATE) BETWEEN Qll.Start_Date_Active AND NVL(Qll.End_Date_Active,TRUNC(SYSDATE)) AND Qlh.Active_flag <> 'N' ) Li ON ( li.inventory_item_id  = org.Inventory_Item_Id AND li.product_uom_code   = org.primary_uom_code) WHERE AS_LEAD_LINES_ALL.LEAD_ID = #v_lead_id